import React from 'react';
import Cuenta from './cuenta';

const ListaCuentas = ({ orders, onCerrarMesa }) => {
  // Obtener mesas únicas y ordenadas
  const mesasConPedidos = [...new Set(
    orders.map(order => order.table ?? order.mesa)
  )].sort((a, b) => a - b);

  // Soporta tanto order.items como order.pedidos
  const getPedidosMesa = (mesa) =>
    orders
      .filter(o => o.table === mesa || o.mesa === mesa)
      .flatMap(o => o.items || o.pedidos || []);

  // Evita errores si hay items indefinidos
  const getTotalMesa = (mesa) => {
    const pedidosValidos = getPedidosMesa(mesa).filter(item => item && item.price !== undefined);
    return pedidosValidos.reduce((sum, item) => sum + Number(item.price || 0), 0).toFixed(2);
  };

  return (
    <div className="lista-cuentas">
      {mesasConPedidos.map((mesa) => (
        <div key={mesa} className="cuenta-wrapper">
          <h2 className="titulo-mesa">Mesa {mesa}</h2>
          <Cuenta 
            mesa={mesa}
            pedidos={getPedidosMesa(mesa)}
            total={getTotalMesa(mesa)}
            onCerrarCuenta={() => onCerrarMesa(mesa)}
          />
        </div>
      ))}
    </div>
  );
};

export default ListaCuentas;
