import React, { useState } from 'react';

const MenuItem = ({ item, onAdd }) => {
  const [excludedIngredients, setExcludedIngredients] = useState([]);

  const handleToggleIngredient = (ingredient) => {
    setExcludedIngredients(prev =>
      prev.includes(ingredient)
        ? prev.filter(i => i !== ingredient)
        : [...prev, ingredient]
    );
  };

  return (
    <div className="menu-item-resta">
      <h3>
        <span>{item.name}</span>
        <span>${item.price.toFixed(2)}</span>
      </h3>

      <div className="ingredients">
        {item.ingredients.map((ingredient, i) => (
          <button
            key={i}
            type="button"
            className={`ingredient-btn ${excludedIngredients.includes(ingredient) ? 'excluded' : ''}`}
            onClick={() => handleToggleIngredient(ingredient)}
          >
            {ingredient}
          </button>
        ))}
      </div>

      <button className="add-button" onClick={() => onAdd(item, excludedIngredients)}>
        AÃ±adir al Carrito
      </button>
    </div>
  );
};

export default MenuItem;
