import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ClientInterface from './pages/ClientInterface';
//import KitchenInterface from './pages/KitchenInterface';
import './styles.css'; // Asegúrate de tener tus estilos globales

function App() {
  return (
    <Router>
      <div className="app-container">
        {/* Barra de navegación opcional */}
        <nav className="app-nav">
          <h1>Sistema de Pedidos - Restaurante</h1>
          <div className="nav-links">
            <a href="/client">Interfaz Cliente</a>
            <a href="/kitchen">Interfaz Cocina</a>
          </div>
        </nav>

        {/* Configuración de rutas */}
        <Routes>
          <Route path="/client" element={<ClientInterface />} />
          
          {/* Ruta por defecto redirige al cliente */}
          <Route path="/" element={<ClientInterface />} />
        </Routes>

        {/* Footer opcional */}
        <footer className="app-footer">
          <p>Sistema desarrollado con React y WebSockets</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
