// src/pages/KitchenInterface.jsx
import React, { useState, useEffect } from 'react';
import KitchenOrders from '../components/KitchenOrders';
import { initializeSocketConnection, markOrderAsCompleted } from '../services/socket';

const dedupById = (arr) => Array.from(new Map((arr || []).map(o => [o.id, o])).values());

const KitchenInterface = () => {
  const [pendingOrders, setPendingOrders] = useState([]);
  const [completedOrders, setCompletedOrders] = useState([]);

  useEffect(() => {
    // initializeSocketConnection(callback) — en tu socket actual
    initializeSocketConnection((payload) => {
      // El admin puede mandar 'menu_updated' -> en nuestro socket lo propagamos como "__MENU_UPDATED__"
      if (payload === '__MENU_UPDATED__') return;

      const ordersArr = Array.isArray(payload) ? payload : [];
      if (!ordersArr.length) return;

      // Separamos por estado y hacemos dedup
      const newPending   = dedupById(ordersArr.filter(o => o.status === 'pending'));
      const newCompleted = dedupById(ordersArr.filter(o => o.status === 'completed'));

      setPendingOrders(prev => dedupById([...prev, ...newPending]));
      setCompletedOrders(prev => dedupById([...prev, ...newCompleted]));
    });
  }, []);

  const handleCompleteOrder = async (orderId) => {
    try {
      await markOrderAsCompleted(orderId); // <- nombre real del export
      // Opcionalmente, actualizamos UI localmente:
      setPendingOrders(prev => prev.filter(o => o.id !== orderId));
      // (si tu WS reenvía el update, también te llegará y caerá en completed)
    } catch (error) {
      console.error('Error al completar pedido:', error);
    }
  };

  return (
    <div className="kitchen-interface">
      <h1 className="kitchen-title">Pedidos en Cocina</h1>
      <div className="orders-container">
        <KitchenOrders
          title="Pedidos Pendientes"
          orders={pendingOrders}
          onCompleteOrder={handleCompleteOrder}
          showCompleteButton
        />
        <KitchenOrders
          title="Pedidos Completados"
          orders={completedOrders}
        />
      </div>
    </div>
  );
};

export default KitchenInterface;
