class SocketService {
  static instance = null;

  static getInstance() {
    if (!SocketService.instance) {
      SocketService.instance = new SocketService();
    }
    return SocketService.instance;
  }

  constructor() {
    this.socket = null;
    this.callbacks = {};
    this.retryCount = 0;
    this.baseUrl = `http://${window.location.hostname}:3001`; // API REST
  }

  connect(callback) {
    try {
      const protocol = window.location.protocol === 'https:' ? 'wss' : 'ws';
      const host = window.location.hostname;
      this.socket = new WebSocket(`${protocol}://${host}:8080`);

      this.socket.onopen = () => {
        console.log('âœ… WebSocket conectado');
        this.retryCount = 0;
        if (callback) callback();
      };

      this.socket.onmessage = (msg) => {
        try {
          const data = JSON.parse(msg.data);
          if (this.callbacks[data.type]) {
            this.callbacks[data.type](data);
          }
        } catch (error) {
          console.error('âŒ Error procesando mensaje:', error);
        }
      };

      this.socket.onclose = () => {
        console.warn('âš ï¸ WebSocket desconectado');
        this.scheduleReconnect();
      };

      this.socket.onerror = (error) => {
        console.error('âŒ Error WebSocket:', error);
      };

    } catch (error) {
      console.error('âŒ Error al conectar:', error);
      this.scheduleReconnect();
    }
  }

  scheduleReconnect() {
    const delay = Math.min(1000 * (2 ** this.retryCount), 30000);
    this.retryCount += 1;
    console.log(`ðŸ” Reintentando conexiÃ³n WebSocket en ${delay / 1000}s...`);
    setTimeout(() => this.connect(), delay);
  }

  on(event, callback) {
    this.callbacks[event] = callback;
  }

  send(data) {
    if (this.socket?.readyState === WebSocket.OPEN) {
      this.socket.send(JSON.stringify(data));
    } else {
      console.error('âŒ WebSocket no estÃ¡ conectado, no se puede enviar el mensaje');
    }
  }

  // MÃ©todos REST API
  async fetchMenu() {
    const response = await fetch(`${this.baseUrl}/dishes`);
    return await response.json();
  }

  async fetchAlcohol() {
    const response = await fetch(`${this.baseUrl}/alcohol`);
    return await response.json();
  }

  async createOrder(order) {
    const response = await fetch(`${this.baseUrl}/orders`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order)
    });
    return await response.json();
  }

  async completeOrder(orderId) {
    const response = await fetch(`${this.baseUrl}/orders/${orderId}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: 'completed' })
    });
    return await response.json();
  }
}

// Exportar instancia Ãºnica
export const socket = SocketService.getInstance();

// Inicializar conexiÃ³n y escuchar eventos
export const initializeSocketConnection = (onOrderUpdate) => {
  socket.on('initial_data', onOrderUpdate);
  socket.on('update', onOrderUpdate);
  socket.connect();
};

// Exportar funciones de uso externo
export const fetchMenuItems = () => socket.fetchMenu();
export const fetchAlcoholItems = () => socket.fetchAlcohol();
export const placeNewOrder = async (order) => {
  await socket.createOrder(order);
  socket.send({ type: 'new_order', order });
};
export const markOrderAsCompleted = async (orderId) => {
  await socket.completeOrder(orderId);
  socket.send({ type: 'complete_order', orderId });
};
