import React from 'react';

const AlcoholCart = ({ cart, onRemove, onPlaceOrder, isTableSelected }) => {
  const total = cart.reduce((sum, item) => {
    let price = item.price;
    if (item.size === 'doble') price *= 1.8;
    if (item.size === 'triple') price *= 2.5;
    return sum + price;
  }, 0).toFixed(2);

  return (
    <div className="alcohol-cart">
      <h2>Carrito</h2>
      {cart.length === 0 ? (
        <p>El carrito está vacío</p>
      ) : (
        <ul>
          {cart.map((item, index) => (
            <li key={index} className="alcohol-cart-item">
              <div>
                <strong>{item.name}</strong> ({item.size}) - ${item.price.toFixed(2)}
              </div>
              <button onClick={() => onRemove(item.id)}>Quitar</button>
            </li>
          ))}
        </ul>
      )}
      <div className="cart-total">
        <p>Total: ${total}</p>
        <button onClick={onPlaceOrder} disabled={!isTableSelected || cart.length === 0}>
          Enviar pedido
        </button>
      </div>
    </div>
  );
};

export default AlcoholCart;
