import React, { useState, useEffect } from 'react';

const Bartender = () => {
  const [orders, setOrders] = useState([]);
  const [showStats, setShowStats] = useState(false);

  useEffect(() => {
    const savedOrders = localStorage.getItem('alcohol_orders');
    if (savedOrders) {
      setOrders(JSON.parse(savedOrders));
    }

    const socket = new WebSocket('ws://192.168.101.102:8080');
    socket.onmessage = (msg) => {
      try {
        const data = JSON.parse(msg.data);
        if (data.type === 'new_order') {
          setOrders((prev) => {
            const updated = [...prev, data.order];
            localStorage.setItem('alcohol_orders', JSON.stringify(updated));
            return updated;
          });
        }
      } catch (e) {
        console.error('Error parsing WebSocket message:', e);
      }
    };
  }, []);

  const getPedidosPorMesa = () => {
    const mesas = {};
    orders.forEach(order => {
      if (!order?.pedidos) return;
      if (!mesas[order.mesa]) mesas[order.mesa] = [];
      mesas[order.mesa].push(...order.pedidos);
    });
    return mesas;
  };

  const calcularTotales = () => {
    let total = 0, simples = 0, dobles = 0, triples = 0;
    orders.forEach(order => {
      if (!Array.isArray(order?.pedidos)) return;
      order.pedidos.forEach(item => {
        const price = item.price || 0;
        if (item.size === 'doble') dobles++;
        else if (item.size === 'triple') triples++;
        else simples++;
        total += price;
      });
    });
    return { total, simples, dobles, triples };
  };

  const pedidosPorMesa = getPedidosPorMesa();
  const stats = calcularTotales();

  return (
    <div className="bartender-interface">
      <h1>Pedidos del Bar</h1>
      {Object.keys(pedidosPorMesa).length === 0 ? (
        <p>No hay pedidos a√∫n.</p>
      ) : (
        Object.entries(pedidosPorMesa).map(([mesa, pedidos]) => (
          <div key={mesa} className="mesa-section">
            <h2>Mesa {mesa}</h2>
            <ul>
              {pedidos.map((item, index) => (
                <li key={index}>
                  {item.name} ({item.size || item.quantity || 1})
                </li>
              ))}
            </ul>
          </div>
        ))
      )}

      <button onClick={() => setShowStats(!showStats)}>
        {showStats ? 'Ocultar resumen de ventas' : 'Mostrar resumen de ventas'}
      </button>

      {showStats && (
        <div className="stats">
          <h3>Resumen de Ventas</h3>
          <p>Simples: {stats.simples}</p>
          <p>Dobles: {stats.dobles}</p>
          <p>Triples: {stats.triples}</p>
          <p><strong>Total: ${stats.total.toFixed(2)}</strong></p>
        </div>
      )}
    </div>
  );
};

export default Bartender;
