// src/pages/ClientInterface.jsx
import React, { useState, useEffect } from 'react';
import TableSelector from '../components/TableroSelector';
import MenuItem from '../components/MenuItem';
import Cart from '../components/cart';
import ListaCuentas from '../components/ListaCuentas';

// ‚úÖ Usa los mismos helpers REST/WS
import { fetchMenuItems, placeNewOrder, initializeSocketConnection } from '../services/socket';
import { guardarCuentaEnJSON, eliminarCuentaDeJSON, cargarCuentasDesdeJSON } from '../services/cuentaService';

// ‚úÖ Dedupe por id (√∫ltimo visto gana)
const dedupById = (arr) => Array.from(new Map((arr || []).map(o => [o.id, o])).values());

export default function ClientInterface() {
  const [selectedTable, setSelectedTable] = useState(null);
  const [cart, setCart] = useState([]);
  const [menu, setMenu] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [orders, setOrders] = useState([]);
  const [showTotalModal, setShowTotalModal] = useState(false);

  // üü¢ Carga inicial de men√∫ + hidrataci√≥n de √≥rdenes
  useEffect(() => {
    const loadMenu = () =>
      fetchMenuItems()
        .then((data) => {
          // ‚ö†Ô∏è Si en db.json hay ids duplicados, esto evita el warning
          //    (a√∫n as√≠ corrige ids en db.json como te indiqu√©)
          const unique = dedupById(data);
          setMenu(unique);
        })
        .catch(console.error);

    loadMenu();

    // Leer √≥rdenes previas desde localStorage
    const saved = localStorage.getItem('orders');
    if (saved) {
      try { setOrders(JSON.parse(saved)); } catch {}
    }

    // Cuentas guardadas en JSON Server (colecci√≥n 'cuentas' si la usas)
    cargarCuentasDesdeJSON().then((cuentas = []) => {
      setOrders(prev => {
        const merged = dedupById([...prev, ...cuentas]);
        localStorage.setItem('orders', JSON.stringify(merged));
        return merged;
      });
    });

    // üõ∞Ô∏è WS: √ìrdenes + aviso de men√∫ actualizado
    initializeSocketConnection((payload) => {
      if (payload === '__MENU_UPDATED__') {
        // Si el admin cambi√≥ el men√∫, refrescamos
        loadMenu();
        return;
      }

      const incoming = Array.isArray(payload) ? payload : [];
      if (!incoming.length) return;

      setOrders(prev => {
        const merged = dedupById([...prev, ...incoming]);
        localStorage.setItem('orders', JSON.stringify(merged));
        return merged;
      });
    });
  }, []);

  // Persistir √≥rdenes en LS cada vez que cambien
  useEffect(() => {
    localStorage.setItem('orders', JSON.stringify(orders));
  }, [orders]);

  // üõí A√±adir al carrito (key interna √∫nica para render del carrito)
  const addToCart = (item, excludedIngredients = []) => {
    setCart(prev => [
      ...prev,
      { ...item, excludedIngredients, id: Date.now() + Math.random().toString(36).slice(2, 9) }
    ]);
  };

  const removeFromCart = (itemId) => {
    setCart(prev => prev.filter(i => i.id !== itemId));
  };

  // üöÄ Enviar pedido
  const placeOrder = async () => {
    if (!selectedTable || cart.length === 0) return;

    const order = {
      id: `${Date.now()}`,            // ‚úÖ dejar que JSON Server autogenere tambi√©n es v√°lido, pero esto es consistente
      table: selectedTable,
      items: cart,
      timestamp: new Date().toISOString(),
      status: 'pending',              // ‚úÖ importante para que Cocina lo muestre
      source: 'kitchen'
    };

    // Guarda por REST y luego notifica por WS
    const saved = await placeNewOrder(order);
    await guardarCuentaEnJSON(saved);

    setOrders(prev => {
      const merged = dedupById([...prev, saved]);
      localStorage.setItem('orders', JSON.stringify(merged));
      return merged;
    });

    setCart([]);
    alert('¬°Pedido enviado!');
  };

  // ‚ùå Cerrar cuenta de una mesa (borra de JSON y estado)
  const closeTableAccount = async (mesa) => {
    const ok = window.confirm(`¬øCerrar cuenta de la mesa ${mesa}?`);
    if (!ok) return;

    const mesaOrders = orders.filter(o => o.table === mesa);
    for (const order of mesaOrders) await eliminarCuentaDeJSON(order.id);

    const updated = orders.filter(o => o.table !== mesa);
    setOrders(updated);
    localStorage.setItem('orders', JSON.stringify(updated));
    alert(`Cuenta de mesa ${mesa} cerrada.`);
  };

  return (
    <div className="client-interface">
      <h1>Men√∫ del Restaurante</h1>

      <input
        type="text"
        placeholder="Buscar platillo o bebida..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-input"
      />

      <TableSelector
        tables={[1,2,3,4,5,6,7,8,8,10,11,12,13,14,15]} // ‚ö†Ô∏è si hay alg√∫n duplicado aqu√≠, igual usamos key robusta
        selectedTable={selectedTable}
        onSelect={setSelectedTable}
      />

      <div className="interface-content">
        <div className="menu-container">
          {menu
            .filter(item =>
              item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
              (item.category || '').toLowerCase().includes(searchTerm.toLowerCase())
            )
            .map((item, idx) => (
              // üõ°Ô∏è key robusta para evitar el warning si se cuela un id duplicado desde el backend
              <MenuItem key={`${item.id}-${item.name}-${idx}`} item={item} onAdd={addToCart} />
            ))}
        </div>

        <div className="cart-container">
          <Cart
            items={cart}
            onRemove={removeFromCart}
            onPlaceOrder={placeOrder}
            isTableSelected={!!selectedTable}
          />
          <button className="show-total-button" onClick={() => setShowTotalModal(true)}>
            Ver Cuentas Activas
          </button>
        </div>
      </div>

      {showTotalModal && (
        <div className="modal">
          <div className="modal-content">
            <ListaCuentas
              orders={orders}
              onCerrarMesa={closeTableAccount}
            />
            <button className="cerrar-btn" onClick={() => setShowTotalModal(false)}>Cerrar</button>
          </div>
        </div>
      )}
    </div>
  );
}
