import React, { useEffect, useMemo, useState } from 'react';
import { initBarSocket } from '../services/BarSocket';
import jsPDF from 'jspdf';
import './bartender.css';

const LEDGER_KEY = 'alcohol_sales_ledger_v1';

function loadLedger() {
  try {
    const raw = localStorage.getItem(LEDGER_KEY);
    if (!raw) {
      return { processedOrderIds: {}, totals: { simples: 0, dobles: 0, triples: 0, botellas: 0, amount: 0 } };
    }
    const parsed = JSON.parse(raw);
    return {
      processedOrderIds: parsed.processedOrderIds || {},
      totals: parsed.totals || { simples: 0, dobles: 0, triples: 0, botellas: 0, amount: 0 },
    };
  } catch {
    return { processedOrderIds: {}, totals: { simples: 0, dobles: 0, triples: 0, botellas: 0, amount: 0 } };
  }
}

function saveLedger(ledger) {
  localStorage.setItem(LEDGER_KEY, JSON.stringify(ledger));
}

const Bartender = () => {
  const [orders, setOrders] = useState([]);
  const [ledger, setLedger] = useState(loadLedger());
  const [showStats, setShowStats] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('alcohol_orders');
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        setOrders(parsed);
        foldOrdersIntoLedger(parsed);
      } catch {}
    }
  }, []);

  useEffect(() => {
    const mergeUnique = (incoming) => {
      setOrders((prev) => {
        const combined = [...prev, ...incoming];
        // si no tiene "completed" lo agregamos en false
        const withFlag = combined.map(o => ({ ...o, completed: o.completed || false }));
        const unique = Array.from(new Map(withFlag.map(o => [o.id, o])).values());
        localStorage.setItem('alcohol_orders', JSON.stringify(unique));
        return unique;
      });
      foldOrdersIntoLedger(incoming);
    };
    initBarSocket(mergeUnique);
  }, []);

  useEffect(() => {
    const onStorage = (e) => {
      if (e.key === 'alcohol_orders' && typeof e.newValue === 'string') {
        try {
          const parsed = JSON.parse(e.newValue);
          setOrders((prev) => {
            const prevIds = new Set(prev.map(o => o.id));
            const changed = parsed.some(o => !prevIds.has(o.id)) || prev.length !== parsed.length;
            return changed ? parsed : prev;
          });
          foldOrdersIntoLedger(parsed);
        } catch {}
      }
    };
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  const foldOrdersIntoLedger = (ordersArr) => {
    if (!Array.isArray(ordersArr) || ordersArr.length === 0) return;

    setLedger((prev) => {
      const next = {
        processedOrderIds: { ...prev.processedOrderIds },
        totals: { ...prev.totals },
      };

      let changed = false;

      for (const order of ordersArr) {
        if (!order?.id || next.processedOrderIds[order.id]) continue;

        const items = order.items ?? order.pedidos ?? [];
        for (const it of items) {
          const size = (it.size || 'simple').toLowerCase();
          const category = (it.category || '').toLowerCase();
          const price = Number(it.price) || 0;

          if (category === 'botellas') {
            next.totals.botellas += Number(it.quantity) || 1;
          } else if (size === 'doble') {
            next.totals.dobles += 1;
          } else if (size === 'triple') {
            next.totals.triples += 1;
          } else {
            next.totals.simples += 1;
          }

          next.totals.amount += price;
          changed = true;
        }

        next.processedOrderIds[order.id] = true;
        changed = true;
      }

      if (changed) saveLedger(next);
      return changed ? next : prev;
    });
  };

  const pedidosPorMesa = useMemo(() => {
    const mesas = {};
    for (const order of orders) {
      const mesa = order.table ?? order.mesa;
      if (mesa == null) continue;
      if (!mesas[mesa]) mesas[mesa] = [];
      const items = order.items ?? order.pedidos ?? [];
      mesas[mesa].push(...items);
    }
    return mesas;
  }, [orders]);

  const statsLive = useMemo(() => {
    let total = 0, simples = 0, dobles = 0, triples = 0, botellas = 0;
    for (const order of orders) {
      const items = order.items ?? order.pedidos ?? [];
      for (const it of items) {
        const size = (it.size || 'simple').toLowerCase();
        const category = (it.category || '').toLowerCase();
        const price = Number(it.price) || 0;

        if (category === 'botellas') {
          botellas += Number(it.quantity) || 1;
        } else if (size === 'doble') {
          dobles++;
        } else if (size === 'triple') {
          triples++;
        } else {
          simples++;
        }
        total += price;
      }
    }
    return { total, simples, dobles, triples, botellas };
  }, [orders]);

  const statsLedger = ledger.totals;

  const resetLedger = () => {
    const empty = { processedOrderIds: {}, totals: { simples: 0, dobles: 0, triples: 0, botellas: 0, amount: 0 } };
    setLedger(empty);
    saveLedger(empty);
  };

  const markAsCompleted = (orderId) => {
    const updated = orders.map(o => o.id === orderId ? { ...o, completed: true } : o);
    setOrders(updated);
    localStorage.setItem('alcohol_orders', JSON.stringify(updated));
  };

  const clearCompletedOrders = () => {
    const updated = orders.filter(o => !o.completed);
    setOrders(updated);
    localStorage.setItem('alcohol_orders', JSON.stringify(updated));
  };

  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text('Historial de Ventas - Bar', 10, 10);

    doc.setFontSize(12);
    doc.text(`Simples: ${statsLedger.simples}`, 10, 20);
    doc.text(`Dobles: ${statsLedger.dobles}`, 10, 30);
    doc.text(`Triples: ${statsLedger.triples}`, 10, 40);
    doc.text(`Botellas: ${statsLedger.botellas}`, 10, 50);
    doc.text(`Total vendido: $${statsLedger.amount.toFixed(2)}`, 10, 60);

    doc.save('historial_ventas_bar.pdf');
  };

  const mesasKeys = Object.keys(pedidosPorMesa).sort((a, b) => Number(a) - Number(b));

  return (
    <div className="bartender-interface">
      <h1>Pedidos del Bar</h1>

      {mesasKeys.length === 0 ? (
        <p>No hay pedidos aÃºn.</p>
      ) : (
        mesasKeys.map((mesa) => (
          <div key={mesa} className="mesa-section">
            <h2>Mesa {mesa}</h2>
            <ul>
              {orders
                .filter(o => (o.table ?? o.mesa) === mesa)
                .map((order) => (
                  <li key={order.id} style={{ textDecoration: order.completed ? 'line-through' : 'none' }}>
                    {order.items?.map((item, idx) => (
                      <span key={idx}>
                        {item.name} {item.size ? `(${item.size})` : ''}
                        {item.category === 'botellas' ? ` x${item.quantity || 1}` : ''}{' '}
                      </span>
                    ))}
                    {!order.completed && (
                      <button
                        style={{ marginLeft: '10px', backgroundColor: '#2ecc71' }}
                        onClick={() => markAsCompleted(order.id)}
                      >
                        âœ… Completar
                      </button>
                    )}
                  </li>
                ))}
            </ul>
          </div>
        ))
      )}

      {orders.some(o => o.completed) && (
        <button
          style={{ backgroundColor: '#e67e22', marginTop: '10px' }}
          onClick={clearCompletedOrders}
        >
          ðŸ—‘ Eliminar todas las completadas
        </button>
      )}

      <button onClick={() => setShowStats(s => !s)}>
        {showStats ? 'Ocultar resumen de ventas' : 'Mostrar resumen de ventas'}
      </button>

      {showStats && (
        <div className="stats">
          <h3>Resumen de Ventas (HistÃ³rico)</h3>
          <p>Simples: {statsLedger.simples}</p>
          <p>Dobles: {statsLedger.dobles}</p>
          <p>Triples: {statsLedger.triples}</p>
          <p>Botellas: {statsLedger.botellas}</p>
          <p><strong>Total: ${statsLedger.amount.toFixed(2)}</strong></p>

          <button style={{ marginTop: 12 }} onClick={downloadPDF}>
            ðŸ“„ Descargar historial en PDF
          </button>

          <details style={{ marginTop: 12 }}>
            <summary>Comparar con ventas abiertas (en vivo)</summary>
            <p>Simples (vivo): {statsLive.simples}</p>
            <p>Dobles (vivo): {statsLive.dobles}</p>
            <p>Triples (vivo): {statsLive.triples}</p>
            <p>Botellas (vivo): {statsLive.botellas}</p>
            <p><strong>Total (vivo): ${statsLive.total.toFixed(2)}</strong></p>
