import React from 'react';

const AlcoholCart = ({ cart, onRemove, onPlaceOrder, isTableSelected, onUpdateCart }) => {
  const handleQuantityChange = (itemId, newQuantity) => {
    const quantity = parseInt(newQuantity);
    if (isNaN(quantity) || quantity <= 0) return;

    onUpdateCart(itemId, quantity);
  };

  const total = cart.reduce((sum, item) => {
    let price = item.price;
    if (item.category.toLowerCase() === 'tragos') {
      if (item.size === 'doble') price = item.basePrice * 2;
      else if (item.size === 'triple') price = item.basePrice * 3;
      else price = item.basePrice;
    } else if (item.category.toLowerCase() === 'botellas') {
      price = item.basePrice * item.quantity;
    }

    return sum + price;
  }, 0).toFixed(2);

  return (
    <div className="alcohol-cart">
      <h2>Carrito</h2>
      {cart.length === 0 ? (
        <p>El carrito está vacío</p>
      ) : (
        <ul>
          {cart.map((item, index) => (
            <li key={index} className="alcohol-cart-item">
              <div>
                <strong>{item.name}</strong>{' '}
                {item.category.toLowerCase() === 'tragos' && `(${item.size})`} - $
                {(item.category.toLowerCase() === 'tragos'
                  ? (item.size === 'doble'
                      ? item.basePrice * 2
                      : item.size === 'triple'
                      ? item.basePrice * 3
                      : item.basePrice)
                  : item.basePrice * item.quantity
                ).toFixed(2)}
              </div>

              {item.category.toLowerCase() === 'botellas' && (
                <input
                  type="number"
                  value={item.quantity}
                  min="1"
                  onChange={(e) => handleQuantityChange(item.id, e.target.value)}
                  style={{ width: '60px', marginRight: '10px' }}
                />
              )}

              <button onClick={() => onRemove(item.id)}>Quitar</button>
            </li>
          ))}
        </ul>
      )}
      <div className="cart-total">
        <p>Total: ${total}</p>
        <button onClick={onPlaceOrder} disabled={!isTableSelected || cart.length === 0}>
          Enviar pedido
        </button>
      </div>
    </div>
  );
};

export default AlcoholCart;
