import React, { useState, useEffect } from 'react';
import KitchenOrders from '../components/KitchenOrders';
import { initializeSocketConnection, markOrderAsCompleted } from '../services/socket';

const KitchenInterface = () => {
  const [pendingOrders, setPendingOrders] = useState([]);
  const [completedOrders, setCompletedOrders] = useState([]);

  useEffect(() => {
    initializeSocketConnection((data) => {
      setPendingOrders(data.orders?.filter(o => o.status === 'pending') || []);
      setCompletedOrders(data.orders?.filter(o => o.status === 'completed') || []);
    });
  }, []);

  const handleCompleteOrder = async (orderId) => {
    try {
      await markOrderAsCompleted(orderId);
    } catch (error) {
      console.error('Error al completar pedido:', error);
    }
  };

  return (
    <div className="kitchen-interface">
      <h1>Cocina</h1>
      <div className="orders-container">
        <KitchenOrders 
          title="Pedidos Pendientes" 
          orders={pendingOrders} 
          onCompleteOrder={handleCompleteOrder}
          showCompleteButton
        />
        <KitchenOrders 
          title="Pedidos Completados" 
          orders={completedOrders} 
        />
      </div>
    </div>
  );
};

export default KitchenInterface;
