// src/services/socket.js
// âœ… Socket genÃ©rico para cocina/cliente + helpers de menÃº en tiempo real
class SocketService {
  static instance = null;

  static getInstance() {
    if (!SocketService.instance) {
      SocketService.instance = new SocketService();
    }
    return SocketService.instance;
  }

  constructor() {
    // URLs dinÃ¡micas segÃºn el host donde se abre el front
    const host = window.location.hostname;
    this.wsUrl = `ws://${host}:8080`;
    this.baseUrl = `http://${host}:3001`;

    this.socket = null;
    this.retryCount = 0;

    // Handlers registrados: { eventType: [fn, fn, ...] }
    this.handlers = {
      initial_data: [],
      update: [],
      menu_updated: [],     // ðŸš¨ avisos de admin (dishes)
    };
  }

  // ConexiÃ³n WS con reintentos exponenciales
  connect() {
    if (this.socket && this.socket.readyState === WebSocket.OPEN) return;

    this.socket = new WebSocket(this.wsUrl);

    this.socket.onopen = () => {
      console.log('âœ… [socket] Conectado:', this.wsUrl);
      this.retryCount = 0;
    };

    this.socket.onmessage = (evt) => {
      let data;
      try {
        data = JSON.parse(evt.data);
      } catch (e) {
        console.error('âŒ [socket] Mensaje invÃ¡lido:', evt.data);
        return;
      }

      const t = data?.type;
      if (!t) return;

      // Disparamos todos los listeners para ese tipo
      (this.handlers[t] || []).forEach(fn => {
        try { fn(data); } catch (e) { console.error('Handler error', e); }
      });

      // Compatibilidad: algunos clientes esperan que 'initial_data'/'update'
      // lleven un array de Ã³rdenes
      if ((t === 'initial_data' || t === 'update') && Array.isArray(data.orders)) {
        // Nada extra, ya lo reciben quienes se suscriben
      }
    };

    this.socket.onclose = () => {
      console.warn('ðŸ”Œ [socket] Desconectado, reintentando...');
      this.scheduleReconnect();
    };

    this.socket.onerror = (err) => {
      console.error('âš ï¸ [socket] Error:', err);
    };
  }

  scheduleReconnect() {
    const delay = Math.min(1000 * (2 ** this.retryCount), 30000);
    this.retryCount += 1;
    setTimeout(() => this.connect(), delay);
  }

  on(type, cb) {
    if (!this.handlers[type]) this.handlers[type] = [];
    this.handlers[type].push(cb);
  }

  send(obj) {
    if (this.socket?.readyState === WebSocket.OPEN) {
      this.socket.send(JSON.stringify(obj));
    } else {
      console.error('ðŸš« [socket] No conectado â€” no se envÃ­a', obj);
    }
  }

  // ---------------- REST: Cocina/Cliente ----------------
  async fetchMenu() {
    const r = await fetch(`${this.baseUrl}/dishes`);
    return r.json();
  }

  async createOrder(order) {
    // Guarda en JSON Server (colecciÃ³n /orders)
    const r = await fetch(`${this.baseUrl}/orders`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order),
    });
    return r.json();
  }

  async completeOrder(orderId) {
    const r = await fetch(`${this.baseUrl}/orders/${orderId}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: 'completed' }),
    });
    return r.json();
  }

  // ---------------- SeÃ±ales de admin ----------------
  notifyMenuUpdated() {
    // Emite un "ping" para que todos refetchen dishes
    this.send({ type: 'menu_updated' });
  }
}

export const socket = SocketService.getInstance();

// Suscribe cocina/cliente a Ã³rdenes + eventos admin de menÃº
export const initializeSocketConnection = (onOrders) => {
  socket.on('initial_data', d => onOrders?.(d.orders || []));
  socket.on('update', d => onOrders?.(d.orders || []));
  // Para refrescar menÃº en tiempo real
  socket.on('menu_updated', () => {
    // Simplemente dejamos que quien lo use haga un refetch cuando lo reciba
    // (ver ClientInterface.jsx)
    onOrders?.('__MENU_UPDATED__'); // banderita por si quieres manejarlo
  });
  socket.connect();
};

// REST helpers
export const fetchMenuItems = () => socket.fetchMenu();
export const placeNewOrder = async (order) => {
  // Guarda y luego notifica al WS
  const saved = await socket.createOrder(order);
  socket.send({ type: 'new_order', order: saved });
  return saved;
};
export const markOrderAsCompleted = async (orderId) => {
  const updated = await socket.completeOrder(orderId);
  socket.send({ type: 'complete_order', orderId });
  return updated;
};

// SeÃ±al de admin (para MenuAdmin.jsx)
export const notifyMenuUpdated = () => socket.notifyMenuUpdated();
