import React, { useState } from 'react';

const MenuItem = ({ item, onAdd }) => {
  const [excludedIngredients, setExcludedIngredients] = useState([]);
  const [selectedChoice, setSelectedChoice] = useState(null);

  const isBebida = (item.category || '').toLowerCase() === 'bebidas';

  const handleToggleIngredient = (ingredient) => {
    if (isBebida) {
      // selección única para bebidas
      setSelectedChoice(prev => (prev === ingredient ? null : ingredient));
    } else {
      // exclusión múltiple para demás categorías
      setExcludedIngredients(prev =>
        prev.includes(ingredient)
          ? prev.filter(i => i !== ingredient)
          : [...prev, ingredient]
      );
    }
  };

  const handleAdd = () => {
    const payload = {
      ...item,
      // Para cocina/registro:
      choice: isBebida ? selectedChoice : null,
      excludedIngredients: isBebida ? [] : excludedIngredients,
      // Si prefieres que en el ticket ya salga “Refresco: Coca Cola”, descomenta:
      // name: isBebida && selectedChoice ? `${item.name}: ${selectedChoice}` : item.name
    };
    onAdd(payload, isBebida ? [] : excludedIngredients);
  };

  return (
    <div className="menu-item-resta">
      <h3>
        <span>{item.name}</span>
        <span>${item.price.toFixed(2)}</span>
      </h3>

      <div className="ingredients">
        {item.ingredients.map((ingredient, i) => {
          const isSelected = isBebida && selectedChoice === ingredient;
          const isExcluded = !isBebida && excludedIngredients.includes(ingredient);
          return (
            <button
              key={i}
              type="button"
              className={`ingredient-btn ${isSelected ? 'selected' : ''} ${isExcluded ? 'excluded' : ''}`}
              onClick={() => handleToggleIngredient(ingredient)}
            >
              {ingredient}
            </button>
          );
        })}
      </div>

      <button
        className="add-button"
        onClick={handleAdd}
        disabled={isBebida && !selectedChoice} // fuerza elegir una opción en bebidas
      >
        Añadir al Carrito
      </button>
    </div>
  );
};

export default MenuItem;
