const CUENTAS_ACTIVAS_KEY = 'cuentas_activas';

export async function cargarCuentasDesdeJSON() {
  const response = await fetch('http://localhost:3001/cuentas');
  return await response.json();
}

export async function guardarCuentaEnJSON(nuevaCuenta) {
  const response = await fetch('http://localhost:3001/cuentas', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(nuevaCuenta),
  });
  return await response.json();
}

export async function eliminarCuentaDeJSON(id) {
  await fetch(`http://localhost:3001/cuentas/${id}`, {
    method: 'DELETE',
  });
}
