import React, { useState } from 'react';

const MenuItem = ({ item, onAdd }) => {
  const [excludedIngredients, setExcludedIngredients] = useState([]);

  const handleIngredientToggle = (ingredient) => {
    if (excludedIngredients.includes(ingredient)) {
      setExcludedIngredients(excludedIngredients.filter(i => i !== ingredient));
    } else {
      setExcludedIngredients([...excludedIngredients, ingredient]);
    }
  };

  const handleAddToCart = () => {
    onAdd(item, excludedIngredients);
    setExcludedIngredients([]); // Resetear selección
  };

  return (
    <div className="menu-item">
      <div className="item-header">
        <h3>{item.name}</h3>
        <span className="price">${item.price.toFixed(2)}</span>
      </div>
      
      <div className="ingredients">
        <p>Ingredientes:</p>
        <ul>
          {item.ingredients.map((ingredient, index) => (
            <li 
              key={index}
              className={excludedIngredients.includes(ingredient) ? 'excluded' : ''}
              onClick={() => handleIngredientToggle(ingredient)}
            >
              {ingredient}
            </li>
          ))}
        </ul>
      </div>

      <button 
        onClick={handleAddToCart}
        className="add-button"
      >
        Añadir al Pedido
      </button>
    </div>
  );
};

export default MenuItem;
