import React from 'react';

// Componente que muestra el carrito de bebidas alcohólicas
const AlcoholCart = ({ cart, onRemove, onPlaceOrder, isTableSelected }) => {
  // Calcula el total del carrito sumando los precios de cada bebida
  const total = cart.reduce((sum, item) => sum + item.price, 0).toFixed(2);

  return (
    <div className="alcohol-cart">
      <h2>Carrito</h2>

      {/* Si el carrito está vacío, muestra un mensaje */}
      {cart.length === 0 ? (
        <p>El carrito está vacío</p>
      ) : (
        <ul>
          {/* Recorre todos los productos en el carrito */}
          {cart.map((item, index) => {
            const isBottle = item.category?.toLowerCase() === 'botellas';

            // Detalles de presentación según el tipo de bebida
            const detail = isBottle
              ? `x${item.quantity} botella${item.quantity > 1 ? 's' : ''}`
              : item.size;

            // Si es botella y cantidad > 1, muestra precio unitario
            const extraInfo = isBottle && item.quantity > 1
              ? ` ($${(item.price / item.quantity).toFixed(2)} c/u)`
              : '';

            return (
              <li key={index} className="alcohol-cart-item">
                <div>
                  <strong>{item.name}</strong> ({detail}) - ${item.price.toFixed(2)}{extraInfo}
                </div>
                <button onClick={() => onRemove(item.id)}>Quitar</button>
              </li>
            );
          })}
        </ul>
      )}

      {/* Sección del total y botón para enviar pedido */}
      <div className="cart-total">
        <p>Total: ${total}</p>
        <button
          onClick={onPlaceOrder}
          disabled={!isTableSelected || cart.length === 0}
        >
          Enviar pedido
        </button>
      </div>
    </div>
  );
};

export default AlcoholCart;
