import React, { useEffect, useState } from 'react';

const Bartender = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const saved = localStorage.getItem('alcohol_orders');
    if (saved) setOrders(JSON.parse(saved));

    const socket = new WebSocket(`ws://${window.location.hostname}:8080`);

    socket.onmessage = (msg) => {
      let data;
      try { data = JSON.parse(msg.data); } catch { return; }

      if (data.type === 'bar:update' && Array.isArray(data.orders)) {
        setOrders(prev => {
          const all = [...prev, ...data.orders];
          const unique = Array.from(new Map(all.map(o => [o.id, o])).values());
          localStorage.setItem('alcohol_orders', JSON.stringify(unique));
          return unique;
        });
      }

      if (data.type === 'bar:initial_data' && Array.isArray(data.orders)) {
        const unique = Array.from(new Map(data.orders.map(o => [o.id, o])).values());
        localStorage.setItem('alcohol_orders', JSON.stringify(unique));
        setOrders(unique);
      }

      if (data.type === 'bar:new_order' && data.order) {
        setOrders(prev => {
          const unique = Array.from(new Map([...prev, data.order].map(o => [o.id, o])).values());
          localStorage.setItem('alcohol_orders', JSON.stringify(unique));
          return unique;
        });
      }
    };
  }, []);

  // ... resto igual, pero usa order.table y order.items si ya unificaste
  return (
    <div className="bartender-interface">
      <h1>Pedidos del Bar</h1>
      {orders.length === 0 ? <p>No hay pedidos a√∫n.</p> : (
        orders.map(order => (
          <div key={order.id} className="mesa-section">
            <h2>Mesa {order.table}</h2>
            <ul>
              {(order.items || []).map((item, i) => (
                <li key={i}>{item.name} {item.size ? `(${item.size})` : ''}</li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default Bartender;
