class SocketService {
  static instance = null;

  static getInstance() {
    if (!SocketService.instance) {
      SocketService.instance = new SocketService();
    }
    return SocketService.instance;
  }

  constructor() {
    this.socket = null;
    this.callbacks = {};
    this.retryCount = 0;

    const hostname = window.location.hostname;
    this.wsUrl = `ws://${hostname}:8080`; // WebSocket din√°mico
    this.baseUrl = `http://${hostname}:3001`; // REST API din√°mico
  }

  connect(callback) {
    try {
      this.socket = new WebSocket(this.wsUrl);

      this.socket.onopen = () => {
        console.log('‚úÖ WebSocket conectado a', this.wsUrl);
        this.retryCount = 0;
        if (callback) callback();
      };

      this.socket.onmessage = (msg) => {
        try {
          const data = JSON.parse(msg.data);
          if (this.callbacks[data.type]) {
            this.callbacks[data.type](data);
          }
        } catch (error) {
          console.error('‚ùå Error procesando mensaje:', error);
        }
      };

      this.socket.onclose = () => {
        console.warn('üîå WebSocket desconectado');
        this.scheduleReconnect();
      };

      this.socket.onerror = (error) => {
        console.error('‚ö†Ô∏è Error WebSocket:', error);
      };

    } catch (error) {
      console.error('‚ùå Error al conectar WebSocket:', error);
      this.scheduleReconnect();
    }
  }

  scheduleReconnect() {
    const delay = Math.min(1000 * (2 ** this.retryCount), 30000);
    this.retryCount += 1;
    setTimeout(() => this.connect(), delay);
  }

  on(event, callback) {
    this.callbacks[event] = callback;
  }

  send(data) {
    if (this.socket?.readyState === WebSocket.OPEN) {
      this.socket.send(JSON.stringify(data));
    } else {
      console.error('üö´ WebSocket no est√° conectado');
    }
  }

  async waitForSocketReady(retries = 5) {
    return new Promise((resolve, reject) => {
      const check = () => {
        if (this.socket?.readyState === WebSocket.OPEN) {
          resolve();
        } else if (retries > 0) {
          setTimeout(() => check(--retries), 500);
        } else {
          reject('WebSocket no disponible');
        }
      };
      check();
    });
  }

  // M√©todos REST
  async fetchMenu() {
    const response = await fetch(`${this.baseUrl}/dishes`);
    return await response.json();
  }

  async fetchAlcohol() {
    const response = await fetch(`${this.baseUrl}/alcohol`);
    return await response.json();
  }

  async createOrder(order) {
    const response = await fetch(`${this.baseUrl}/orders`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order)
    });
    return await response.json();
  }

  async completeOrder(orderId) {
    const response = await fetch(`${this.baseUrl}/orders/${orderId}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: 'completed' })
    });
    return await response.json();
  }
}

// Instancia Singleton
export const socket = SocketService.getInstance();

// Inicializa conexi√≥n WebSocket
export const initializeSocketConnection = (onOrderUpdate) => {
  socket.on('initial_data', (data) => {
    if (onOrderUpdate) onOrderUpdate(data);
  });
  socket.on('update', (data) => {
    if (onOrderUpdate) onOrderUpdate(data);
  });
  socket.connect();
};

// Funciones para usar en Bar y Bartender
export const fetchMenuItems = () => socket.fetchMenu();
export const fetchAlcoholItems = () => socket.fetchAlcohol();

export const placeNewOrder = async (order) => {
  try {
    await socket.createOrder(order);
    await socket.waitForSocketReady(); // üîÅ Esperar que el socket est√© conectado
    socket.send({ type: 'new_order', order });
    console.log('üì¶ Pedido enviado v√≠a WebSocket:', order);
  } catch (err) {
    console.error('‚ùå No se pudo enviar pedido por WebSocket:', err);
  }
};

export const markOrderAsCompleted = async (orderId) => {
  try {
    await socket.completeOrder(orderId);
    await socket.waitForSocketReady(); // üîÅ Esperar socket antes de enviar
    socket.send({ type: 'complete_order', orderId });
  } catch (err) {
    console.error('‚ùå No se pudo enviar confirmaci√≥n de completado:', err);
  }
};
