import React from 'react';

const KitchenOrders = ({ title, orders, onCompleteOrder, showCompleteButton }) => {
  return (
    <div className="kitchen-orders">
      <h2 className="orders-title">{title} ({orders.length})</h2>

      {orders.length === 0 ? (
        <p className="no-orders">No hay {title.toLowerCase()}</p>
      ) : (
        <div className="orders-list">
          {orders.map(order => (
            <div key={order.id} className={`order-card ${order.status}`}>
              <div className="order-header">
                <span className="order-table">Mesa {order.table}</span>
                <span className="order-time">{new Date(order.timestamp).toLocaleTimeString()}</span>
              </div>

              <ul className="order-items">
                {order.items.map((item, index) => (
                  <li key={`${item.name}-${index}`} className="order-item">
                    <span className="item-name">{item.name}</span>
                    {item.excludedIngredients?.length > 0 && (
                      <span className="excluded"> (Sin: {item.excludedIngredients.join(', ')})</span>
                    )}
                  </li>
                ))}
              </ul>

              {showCompleteButton && (
                <button 
                  onClick={() => onCompleteOrder(order.id)}
                  className="complete-button"
                >
                  Marcar como Completado
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default KitchenOrders;
