// Reemplaza listas en cada update para no “revivir” pedidos viejos
import React, { useState, useEffect } from 'react';
import KitchenOrders from '../components/KitchenOrders';
import { initializeSocketConnection, markOrderAsCompleted } from '../services/socket';

const KitchenInterface = () => {
  const [pendingOrders, setPendingOrders] = useState([]);
  const [completedOrders, setCompletedOrders] = useState([]);

  useEffect(() => {
    initializeSocketConnection((payload) => {
      if (payload === '__MENU_UPDATED__') return;
      const ordersArr = Array.isArray(payload) ? payload : [];
      const newPending   = ordersArr.filter(o => o.status === 'pending');
      const newCompleted = ordersArr.filter(o => o.status === 'completed');
      setPendingOrders(newPending);      // 👈 reemplazar
      setCompletedOrders(newCompleted);  // 👈 reemplazar
    });
  }, []);

  const handleCompleteOrder = async (orderId) => {
    try {
      await markOrderAsCompleted(orderId);
      setPendingOrders(prev => prev.filter(o => o.id !== orderId));
    } catch (error) { console.error('Error al completar pedido:', error); }
  };

  return (
    <div className="kitchen-interface">
      <h1 className="kitchen-title">Pedidos en Cocina</h1>
      <div className="orders-container">
        <KitchenOrders title="Pedidos Pendientes"   orders={pendingOrders}   onCompleteOrder={handleCompleteOrder} showCompleteButton />
        <KitchenOrders title="Pedidos Completados"  orders={completedOrders} />
      </div>
    </div>
  );
};
export default KitchenInterface;
