// src/services/BarSocket.js
// âœ… Socket aparte para BAR (alcohol) y seÃ±ales de "alcohol_updated"
class BarSocket {
  static instance = null;

  static getInstance() {
    if (!BarSocket.instance) {
      BarSocket.instance = new BarSocket();
    }
    return BarSocket.instance;
  }

  constructor() {
    const host = window.location.hostname;
    this.wsUrl = `ws://${host}:8080`;
    this.baseUrl = `http://${host}:3001`;
    this.socket = null;
    this.retryCount = 0;
    this.handlers = {
      'bar:new_order': [],
      'alcohol_updated': [],
    };
  }

  connect() {
    if (this.socket && this.socket.readyState === WebSocket.OPEN) return;

    this.socket = new WebSocket(this.wsUrl);

    this.socket.onopen = () => {
      console.log('ðŸ¹ [bar-socket] Conectado:', this.wsUrl);
      this.retryCount = 0;
    };

    this.socket.onmessage = (evt) => {
      let data;
      try { data = JSON.parse(evt.data); } catch { return; }
      const t = data?.type;
      if (!t) return;

      (this.handlers[t] || []).forEach(fn => {
        try { fn(data); } catch (e) { console.error(e); }
      });
    };

    this.socket.onclose = () => {
      console.warn('ðŸ¹ [bar-socket] Desconectado, reintentando...');
      this.scheduleReconnect();
    };
    this.socket.onerror = (e) => console.error('ðŸ¹ [bar-socket] Error:', e);
  }

  scheduleReconnect() {
    const d = Math.min(1000 * (2 ** this.retryCount), 30000);
    this.retryCount += 1;
    setTimeout(() => this.connect(), d);
  }

  on(type, cb) {
    if (!this.handlers[type]) this.handlers[type] = [];
    this.handlers[type].push(cb);
  }

  send(obj) {
    if (this.socket?.readyState === WebSocket.OPEN) {
      this.socket.send(JSON.stringify(obj));
    } else {
      console.error('ðŸš« [bar-socket] No conectado â€” no se envÃ­a', obj);
    }
  }

  // --------- REST: catÃ¡logo alcohol ---------
  async fetchAlcohol() {
    const r = await fetch(`${this.baseUrl}/alcohol`);
    return r.json();
  }

  // --------- SeÃ±ales ---------
  notifyAlcoholUpdated() {
    this.send({ type: 'alcohol_updated' });
  }
}

export const barSocket = BarSocket.getInstance();

// Inicializa WS bar + listener de eventos
export const initBarSocket = (onOrdersUpdate, onAlcoholUpdated) => {
  // nuevos pedidos del bar (si algÃºn cliente los emite namespaced)
  barSocket.on('bar:new_order', d => {
    const order = d.order ? [d.order] : [];
    onOrdersUpdate?.(order);
  });

  // cambios del catÃ¡logo de alcohol
  barSocket.on('alcohol_updated', () => onAlcoholUpdated?.());

  barSocket.connect();
};

// Refrescar catÃ¡logo alcohol (GET)
export const fetchAlcoholItems = () => barSocket.fetchAlcohol();

// Enviar pedido del bar (WS-only broadcast, sin tocar /orders)
export const placeBarOrder = async (order) => {
  // AquÃ­ solito emitimos por WS para que Bartender lo reciba
  barSocket.send({ type: 'bar:new_order', order });
  return order; // devolvemos lo mismo para aÃ±adirlo localmente
};

// SeÃ±al admin (AlcoholAdmin.jsx) tras CRUD
export const notifyAlcoholUpdated = () => barSocket.notifyAlcoholUpdated();
