// client/src/services/socket.js
class SocketService {
  static instance = null;

  static getInstance() {
    if (!SocketService.instance) {
      SocketService.instance = new SocketService();
    }
    return SocketService.instance;
  }

  constructor() {
    this.socket = null;
    this.callbacks = {};
    this.retryCount = 0;

    const hostname = window.location.hostname;
    this.wsUrl = `ws://${hostname}:8080`;
    this.baseUrl = `http://${hostname}:3001`;
  }

  connect(callback) {
    try {
      this.socket = new WebSocket(this.wsUrl);

      this.socket.onopen = () => {
        console.log('âœ… WebSocket conectado a', this.wsUrl);
        this.retryCount = 0;
        if (callback) callback();
      };

      this.socket.onmessage = (msg) => {
        try {
          const data = JSON.parse(msg.data);
          if (data?.type && this.callbacks[data.type]) {
            this.callbacks[data.type](data);
          }
        } catch (error) {
          console.error('âŒ Error procesando mensaje:', error);
        }
      };

      this.socket.onclose = () => {
        console.warn('ðŸ”Œ WebSocket desconectado');
        this.scheduleReconnect();
      };

      this.socket.onerror = (error) => {
        console.error('âš ï¸ Error WebSocket:', error);
      };

    } catch (error) {
      console.error('âŒ Error al conectar WebSocket:', error);
      this.scheduleReconnect();
    }
  }

  scheduleReconnect() {
    const delay = Math.min(1000 * (2 ** this.retryCount), 30000);
    this.retryCount += 1;
    setTimeout(() => this.connect(), delay);
  }

  on(event, callback) {
    this.callbacks[event] = callback;
  }

  send(data) {
    if (this.socket?.readyState === WebSocket.OPEN) {
      this.socket.send(JSON.stringify(data));
    } else {
      console.error('ðŸš« WebSocket no estÃ¡ conectado');
    }
  }

  // REST
  async fetchMenu() {
    const res = await fetch(`${this.baseUrl}/dishes`);
    return await res.json();
  }

  async fetchAlcohol() {
    const res = await fetch(`${this.baseUrl}/alcohol`);
    return await res.json();
  }

  async createOrder(order) {
    const res = await fetch(`${this.baseUrl}/orders`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order)
    });
    return await res.json();
  }

  async completeOrder(orderId) {
    const res = await fetch(`${this.baseUrl}/orders/${orderId}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: 'completed' })
    });
    return await res.json();
  }

  // ðŸ”” NUEVO: avisos tiempo real de cambios de menÃºs
  notifyMenuUpdated() {
    this.send({ type: 'menu_updated' });
  }

  notifyAlcoholUpdated() {
    this.send({ type: 'alcohol_updated' });
  }
}

export const socket = SocketService.getInstance();

export const initializeSocketConnection = (onOrderUpdate) => {
  socket.on('initial_data', (data) => onOrderUpdate?.(data));
  socket.on('update', (data) => onOrderUpdate?.(data));
  socket.connect();
};

export const fetchMenuItems = () => socket.fetchMenu();
export const fetchAlcoholItems = () => socket.fetchAlcohol();

export const placeNewOrder = async (order) => {
  await socket.createOrder(order);
  socket.send({ type: 'new_order', order });
};

export const markOrderAsCompleted = async (orderId) => {
  await socket.completeOrder(orderId);
  socket.send({ type: 'complete_order', orderId });
};

// ðŸ”” Export helpers para admins
export const notifyMenuUpdated = () => socket.notifyMenuUpdated();
export const notifyAlcoholUpdated = () => socket.notifyAlcoholUpdated();
