import React from 'react';

const AlcoholCart = ({ cart, onRemove, onPlaceOrder, isTableSelected, onUpdateQuantity }) => {
  // Calculamos el total en base al tamaño o cantidad
  const total = cart.reduce((sum, item) => {
    let price = item.price;

    // Si es trago, usamos size
    if (item.size === 'doble') price *= 1.8;
    if (item.size === 'triple') price *= 2.5;

    // Si es botella, usamos quantity
    if (item.category?.toLowerCase() === 'botellas') {
      price = item.price * item.quantity;
    }

    return sum + price;
  }, 0).toFixed(2);

  return (
    <div className="alcohol-cart">
      <h2>Carrito</h2>

      {cart.length === 0 ? (
        <p>El carrito está vacío</p>
      ) : (
        <ul>
          {cart.map((item, index) => (
            <li key={index} className="alcohol-cart-item">
              <div>
                <strong>{item.name}</strong>

                {/* Mostrar tipo de trago si aplica */}
                {item.category?.toLowerCase() === 'tragos' && (
                  <span> ({item.size})</span>
                )}

                {/* Mostrar cantidad si es botella */}
                {item.category?.toLowerCase() === 'botellas' && (
                  <div className="quantity-input">
                    <label>Cantidad:</label>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => onUpdateQuantity(item.id, parseInt(e.target.value))}
                    />
                  </div>
                )}

                {/* Mostrar precio */}
                <div>
                  ${(
                    item.category?.toLowerCase() === 'botellas'
                      ? item.price * item.quantity
                      : item.size === 'doble'
                      ? item.price * 1.8
                      : item.size === 'triple'
                      ? item.price * 2.5
                      : item.price
                  ).toFixed(2)}
                </div>
              </div>

              <button onClick={() => onRemove(item.id)}>Quitar</button>
            </li>
          ))}
        </ul>
      )}

      <div className="cart-total">
        <p>Total: ${total}</p>
        <button onClick={onPlaceOrder} disabled={!isTableSelected || cart.length === 0}>
          Enviar pedido
        </button>
      </div>
    </div>
  );
};

export default AlcoholCart;
