// services/cuentaService.js
const base = `http://${window.location.hostname}:3001`;

export async function cargarCuentasDesdeJSON() {
  const r = await fetch(`${base}/cuentas`);
  return r.json();
}

export async function guardarCuentaEnJSON(cuenta) {
  const r = await fetch(`${base}/cuentas`, {
    method: 'POST',
    headers: {'Content-Type':'application/json'},
    body: JSON.stringify(cuenta),
  });
  return r.json();
}

export async function eliminarCuentaDeJSON(id) {
  await fetch(`${base}/cuentas/${id}`, { method: 'DELETE' });
}
